import json
from typing import Tuple
from tinygrad import Tensor, nn
from tinygrad.nn.optim import SGD

import unittest

class TestBug(unittest.TestCase):
    ALPHA = 10

    def optimal_mse(self, y_pred: Tensor, y_true: Tensor, weights: Tensor) -> Tensor:
        mse = (y_pred - y_true).pow(2).mean()

        nonzero_penalty = (self.ALPHA * weights).abs().sum() / (weights.shape[0])

        return mse + nonzero_penalty # Add it so if it is smaller, loss is smaller

    class Model:
      def __init__(self):
        self.l1 = nn.Conv2d(1, 3, 2)
        self.l2 = nn.Conv2d(3, 6, 2)
        self.l3 = nn.Linear(216, 150)
        self.l4 = nn.Linear(150, 30*30)

      def __call__(self, x:Tensor) -> Tensor:
        x = self.l1(x).relu().max_pool2d((2,2))
        x = self.l2(x).relu().max_pool2d((2,2))
        x=  self.l3(x.flatten(1).dropout(0.5))
        return self.l4(x).reshape(30, 30)

    def test_bug(self):
        net = self.Model()

        opt = SGD([net.l1.weight, net.l2.weight, net.l3.weight, net.l4.weight], lr=3e-4)


